package com.macom.medicationapp;import android.app.AlarmManager;import android.app.NotificationManager;import android.app.PendingIntent;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.view.animation.Animation;import android.view.animation.AnimationUtils;import android.widget.ImageView;import android.widget.TextView;import androidx.appcompat.app.AppCompatActivity;public class AnimationAlarm extends AppCompatActivity {	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.activity_animation_alarm);		ImageView image = (ImageView) findViewById(R.id.alarm);		Animation animation1 =				AnimationUtils.loadAnimation(getApplicationContext(),						R.anim.anim_blink);		image.startAnimation(animation1);		animation1.reset();		TextView tv = (TextView) findViewById(R.id.textanim);		tv.clearAnimation();		tv.startAnimation(animation1);	}	public void stopAlarm(View view) {		stopService(new Intent(AnimationAlarm.this, AlarmSoundService.class));		NotificationManager notificationManager = (NotificationManager) this				.getSystemService(Context.NOTIFICATION_SERVICE);		notificationManager.cancel(AlarmNotificationService.NOTIFICATION_ID);		finish();	}}