package com.macom.medicationapp.Animations;import android.app.NotificationManager;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.view.animation.Animation;import android.view.animation.AnimationUtils;import android.widget.ImageView;import android.widget.TextView;import androidx.appcompat.app.AppCompatActivity;import com.macom.medicationapp.R;import com.macom.medicationapp.ServicesAndRecievers.AlarmNotificationService;import com.macom.medicationapp.ServicesAndRecievers.AlarmSoundService;import butterknife.BindView;import butterknife.ButterKnife;public class AnimationAlarm extends AppCompatActivity {    @BindView(R.id.alarm)    ImageView image;    @BindView(R.id.textanim)    TextView tv;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_animation_alarm);        Animation animation1 =                AnimationUtils.loadAnimation(getApplicationContext(),                        R.anim.anim_blink);        ButterKnife.bind(this);        image.startAnimation(animation1);        animation1.reset();        tv.clearAnimation();        tv.startAnimation(animation1);    }    public void stopAlarm(View view) {        stopService(new Intent(AnimationAlarm.this, AlarmSoundService.class));        NotificationManager notificationManager = (NotificationManager) this                .getSystemService(Context.NOTIFICATION_SERVICE);        notificationManager.cancel(AlarmNotificationService.NOTIFICATION_ID);        finish();    }}